mapper.select:
SELECT wc.id AS wallet_id, wc.credit_balance, wc.credit_quota, wc.credit_quota - wc.credit_balance as credit_used, wc.`status`, t.* FROM wallet_credit wc, ( SELECT cm.id AS company_member_id, cm.`name`, cm.ic_no, cm.department, cm.employee_no FROM company c, company_member cm WHERE c.id = #{companyId} AND cm.company = c.id
AND cm.department = #{department}
AND cm.`name` = #{name}
) t WHERE wc.company_member = t.company_member_id ORDER BY wallet_id DESC
